import React, { Component } from "react";

let dialogStyles = {
  width: "500px",
  maxWidth: "100%",
  margin: "0 auto",
  position: "fixed",
  left: "50%",
  transform: "translate(-50%, -50%)",
  zIndex: "999",
  backgroundColor: "#eee",
  padding: "10px 20px 40px",
  borderRadius: "8px",
  display: "flex",
  flexDirection: "column"
};

let dialogCloseButton = {
  padding: "5px",
  width: "25px",
  cursor: "pointer",
  borderRadius: "50%",
  border: "none",
  fontWeight: "bold",
  float: "right",
  backgroundColor: "red",
  outline: "none",
  float: "right",
  alignSelf: "flex-end"
  // color: "red"
};

let dialog;

class EditDialog extends Component {
  render() {
    let dialog = (
      <div style={dialogStyles}>
        <button style={dialogCloseButton} onClick={this.props.setFalse}>
          X
        </button>
        <form>
          <input type="text" placeholder="Edit Todo" />
          <input type="submit" />
        </form>
      </div>
    );

    if (!this.props.isOpen) {
      dialog = null;
    }
    return <div>{dialog}</div>;
  }
}

export default EditDialog;
