import React, { Component } from "react";
import { Z_FIXED } from "zlib";

let dialogStyles = {
  width: "500px",
  maxWidth: "100%",
  margin: "0 auto",
  position: "fixed",
  left: "50%",
  transform: "translate(-50%, -50%)",
  zIndex: "999",
  backgroundColor: "#eee",
  padding: "10px 20px 40px",
  borderRadius: "8px",
  display: "flex",
  flexDirection: "column"
};

let dialogCloseButton = {
  marginBottom: "15px",
  padding: "5px",
  cursor: "pointer",
  borderRadius: "50%",
  border: "none",
  fontWeight: "bold"
};

let dialog;

class EditDialog extends Component {
  render() {
    let dialog = (
      <div style={dialogStyles}>
        <p>I'm a dialogeee.</p>
      </div>
    );

    if (!this.props.isOpen) {
      dialog = null;
    }
    return <div>{dialog}</div>;
  }
}

export default EditDialog;
