import React from "react";
// import { BrowserRouter as Router, withRouter } from "react-router-dom";
import "../../src/App.css";
import Todos from "./Todos";
// import Header from "./layout/Header";
import AddTodo from "./AddTodo";
// import About from "./pages/About";
import TodoStatus from "./TodoStatus";
// import uuid from "uuid";
import axios from "axios";

class TodoMain extends React.Component {
  constructor() {
    super();
    this.state = {
      todos: []
    };
  }

  componentDidMount() {
    axios
      .get("/api/todos/", {
        headers: { Authorization: localStorage.usertoken }
      })
      .then(res => this.setState({ todos: res.data }));
  }

  markComplete = id => {
    // axios
    //   .put(`/api/todo/${id}`, {
    //     headers: { Authorization: localStorage.usertoken }
    //   })
    axios({
      method: "put",
      url: `/api/marktodo/${id}`,
      headers: {
        Authorization: localStorage.usertoken
      }
    }).then(res =>
      this.setState({
        todos: this.state.todos.map(todo => {
          if (id === todo.id) {
            todo.completed = !todo.completed;
          }
          return todo;
        })
      })
    );
  };

  delTodo = id => {
    axios.delete(`api/todo/${id}`).then(res =>
      this.setState({
        todos: this.state.todos.filter(todo => id !== todo.id)
      })
    );
  };

  addTodo = title => {
    // const newTodo = {
    //   id: uuid.v4(),
    //   title,
    //   completed: false
    // };

    axios
      .post(
        "api/todo",
        {
          title,
          completed: false
        },
        {
          headers: { Authorization: localStorage.usertoken }
        }
      )
      .then(res => this.setState({ todos: [...this.state.todos, res.data] }));
    // this.setState({ todos: [...this.state.todos, newTodo] });
  };

  calTask = task => {
    let cList = this.state.todos.filter(todo => {
      return todo.completed === true;
    });
    if (task === "cTask") {
      return cList.length;
    } else if (task === "rTask") {
      return this.state.todos.length - cList.length;
    }
  };

  // rTask = () => {
  //   let cList = this.state.todos.filter(todo => {
  //     return todo.completed === true;
  //   });
  //   let rList = this.state.todos.length - cList.length;
  //   return rList;
  // }

  editTodo = (id, title) => {
    var eTodo = prompt("Edit todo:", title);
    if (eTodo === null) {
      return;
    }

    axios({
      method: "put",
      url: `/api/todo/${id}`,
      data: {
        title: eTodo,
        completed: false
      }
    }).then(res =>
      this.setState({
        todos: this.state.todos.map(todo => {
          if (id === todo.id) {
            todo.title = eTodo;
            todo.completed = false;
          }
          return todo;
        })
      })
    );

    // console.log(id, title);
  };

  render() {
    return (
      <div className="App">
        <div className="container">
          <AddTodo addTodo={this.addTodo} />
          <Todos
            todos={this.state.todos}
            delTodo={this.delTodo}
            markComplete={this.markComplete.bind(this)}
            editTodo={this.editTodo.bind(this)}
          />
          <TodoStatus calTask={this.calTask} />
        </div>
      </div>
    );
  }
}

export default TodoMain;
