import React from "react";
import { BrowserRouter as Router, Route } from "react-router-dom";
import "./App.css";
import Todos from "./components/Todos";
import Header from "./components/layout/Header";
import AddTodo from "./components/AddTodo";
import About from "./components/pages/About";
import TodoStatus from "./components/TodoStatus";
// import uuid from "uuid";
import axios from "axios";

class App extends React.Component {
  state = {
    todos: []
  };

  componentDidMount() {
    axios.get("/api/todos").then(res => this.setState({ todos: res.data }));
  }

  markComplete(id) {
    this.setState({
      todos: this.state.todos.map(todo => {
        if (id === todo.id) {
          todo.completed = !todo.completed;
        }
        return todo;
      })
    });
    // console.log(id);
    axios.put(`api/todo/${id}`, { completed: !todo.completed });
  }

  delTodo = id => {
    axios.delete(`api/todo/${id}`).then(res =>
      this.setState({
        todos: this.state.todos.filter(todo => id !== todo.id)
      })
    );
  };

  addTodo = title => {
    // const newTodo = {
    //   id: uuid.v4(),
    //   title,
    //   completed: false
    // };

    axios
      .post("api/todo", {
        title,
        completed: false
      })
      .then(res => this.setState({ todos: [...this.state.todos, res.data] }));
    // this.setState({ todos: [...this.state.todos, newTodo] });
  };

  calTask = task => {
    let cList = this.state.todos.filter(todo => {
      return todo.completed === true;
    });
    if (task === "cTask") {
      return cList.length;
    } else if (task === "rTask") {
      return this.state.todos.length - cList.length;
    }
  };

  // rTask = () => {
  //   let cList = this.state.todos.filter(todo => {
  //     return todo.completed === true;
  //   });
  //   let rList = this.state.todos.length - cList.length;
  //   return rList;
  // }

  render() {
    return (
      <Router>
        <div className="App">
          <div className="container">
            <Header />

            <Route
              exact
              path="/"
              render={props => (
                <React.Fragment>
                  <AddTodo addTodo={this.addTodo} />
                  <Todos
                    todos={this.state.todos}
                    delTodo={this.delTodo}
                    markComplete={this.markComplete.bind(this)}
                  />
                  <TodoStatus calTask={this.calTask} />
                </React.Fragment>
              )}
            />

            <Route path="/about" component={About} />
          </div>
        </div>
      </Router>
    );
  }
}

export default App;
